root.script:
$( "#GemImage" ).AddClass("rarity-normal rarity-good rarity-best rarity-rare rarity-unique rarity-epic");

function SetRarity( rarity )
{
	$( "#GemImage" ).SetHasClass("rarity-normal rarity-good rarity-best rarity-rare rarity-unique rarity-epic", false);

	var rarityClass = "rarity-normal";
	switch( rarity )
	{
		case 1:
			rarityClass = "rarity-good";
			break;

		case 2:
			rarityClass = "rarity-best";
			break;

		case 3:
			rarityClass = "rarity-rare";
			break;

		case 4:
			rarityClass = "rarity-unique";
			break;

		case 5:
			rarityClass = "rarity-epic";
			break;
	}

	$( "#GemImage" ).SetHasClass(rarityClass, true )
}

function AddDesc()
{

}

function Update( id )
{
	SetRarity( 1 );
}

$.GetContextPanel().Update = Update;
